import React from 'react'
import { storiesOf } from '@storybook/react'
import FeedPanel from './Articles'
import InitStories from 'helpers/hoc/initStories'
import { withKnobs, text, boolean } from '@storybook/addon-knobs'
import { action } from '@storybook/addon-actions'
import backgrounds from '@storybook/addon-backgrounds'

storiesOf('Articles', module)
  .addDecorator(story =>
    (<InitStories>
      {story()}
    </InitStories>)
  )
  .addDecorator(withKnobs)
  .addDecorator(
    backgrounds([
      {
        name: 'transparent',
        value:
          "url('data:image/png;base64,')",
        default: true,
      },
      { name: 'light', value: '#E0E4CC' },
      { name: 'dark', value: '#402020' },
      { name: 'contrast', value: '#FF8B8B' },
    ])
  )
  .add('without props', () =>
    (<FeedPanel
      article={{
        source: { name: 'test' },
        feed: [{ title: 'test', description: 'description' }],
        isLoading: true,
        remove: action('Remove'),
      }}
    />)
  )
  .add('with some props', () => <FeedPanel text="The Comp" />)
